import * as anchor from "@project-serum/anchor";
import { TOKEN_PROGRAM_ID } from '@solana/spl-token';
import {
    Connection,
  } from "@solana/web3.js";
import { abort } from "process";

const connection = new anchor.web3.Connection(
	"https://api.devnet.solana.com",
	"confirmed"
)

const METADATA_PREFIX = "metadata";
const METADATA_PROGRAM_ID = "metaqbxxUerdq28cj1RbAWkYQm3ybzjb6a8bt518x1s";


const loadMintData = (walletAddress) => {
	let tokenAddresses = getTokenAddressesInWallet(walletAddress);
	let tokenData = getMultipleTokenData(tokenAddresses.slice(0, 32));
	let tokens = [];
	for(let { data } of tokenData.filter(e => e)) {
		const mint = decodeMetadata(data[0])	
		console.log(mint)
	}
}

async function getTokenAddressesInWallet(walletAddress){
	let response = await connection._rpcRequest(
		"getTokenAccountsByOwner",
		[
			walletAddress,
			{ programId: "TokenkegQfeZyiNwAJbNbGKPFXCWuBvf9Ss623VQ5DA" },
			{
				encoding: "base64",
				dataSlice: { offset: 0, length: 32 }
			}
		]
	).then(e => e.result.value);
	
	return response.map(e =>
		PublicKey.from(stringToBuffer(atob(e.account.data[0]))).toString()
	)
}

async function getMultipleTokenData(tokens, from = 0, to = tokens.length){
	let metadataAddresses = [];
	for(let i = from; i < to; i++)
		metadataAddresses[i] = await getMetadataAddress(tokens[i]);
	
	return connection._rpcRequest(
		"getMultipleAccounts",
		[
			metadataAddresses,
			{ encoding: "base64" }
		]
	).then(e => e.result.value);
}

async function decodeMetadata(dataBase64){
	let metadataUri = atob(dataBase64).match(/https?:\/\/.*?(?=\0)/);
	if(!metadataUri)
		return null;
	return fetch(metadataUri).then(e => e.json());
}

async function getMetadataAddress(tokenAddress){
	let metadataProgramIdKey = PublicKey.from(METADATA_PROGRAM_ID);
	let [address, nonce] = await PublicKey.findProgramAddress(
		[
			stringToBuffer(METADATA_PREFIX),
			metadataProgramIdKey.toBuffer(),
			PublicKey.from(tokenAddress).toBuffer()
		],
		metadataProgramIdKey
    );
	
	return address.toBase58();
}

const PublicKey = solanaWeb3.PublicKey;
const PUBLIC_KEY_CACHE = {};
PublicKey.from = (input) => {
	let publicKey = null;
	if(typeof input == "string" || input instanceof Uint8Array)
		publicKey = new PublicKey(input);
	else
		throw new Error(`Could not create PublicKey from: "${input}"`);
	
	key = publicKey.toBase58();
	if(!(key in PUBLIC_KEY_CACHE))
		PUBLIC_KEY_CACHE[key] = publicKey;
	return PUBLIC_KEY_CACHE[key];
}

function stringToBuffer(string){
	return Uint8Array.from(string, e => e.charCodeAt(0));
}

export default loadMintData
