import * as anchor from "@project-serum/anchor";
import {
    PublicKey,
    Connection
} from "@solana/web3.js";
import {
  TOKEN_PROGRAM_ID
} from "@solana/spl-token";

const TOKEN_METADATA_PROGRAM_ID = new anchor.web3.PublicKey(
    "metaqbxxUerdq28cj1RbAWkYQm3ybzjb6a8bt518x1s"
);

const SPL_TOKEN_PROGRAM = new anchor.web3.PublicKey(
    "TokenkegQfeZyiNwAJbNbGKPFXCWuBvf9Ss623VQ5DA"
);

// Exported function
async function getTokenAccounts (
    wallet: PublicKey,
    connect: anchor.web3.Connection
): Promise<any> {

    const tokenAccounts = connect.getParsedTokenAccountsByOwner(
        wallet,
        {programId: TOKEN_PROGRAM_ID},
    ).then(e => e.value)

    console.log(tokenAccounts)

    const getMint = async (
        tokenAccountPubkey: PublicKey
    ) => {
        const tokenMintData = (
            await connect.getParsedAccountInfo(
                tokenAccountPubkey,
                "singleGossip"
            )
        ).value!.data;
        //@ts-expect-error (doing the data parsing into steps so this ignore line is not moved around by formatting)
        const tokenMintAddress = tokenMintData.parsed.info.mint;

        return new PublicKey(tokenMintAddress)
    }
    console.log(getMint)


}

