    "metaqbxxUerdq28cj1RbAWkYQm3ybzjb6a8bt518x1s"
);

const SPL_TOKEN_PROGRAM = new anchor.web3.PublicKey(
    "TokenkegQfeZyiNwAJbNbGKPFXCWuBvf9Ss623VQ5DA"
);

// Exported function
async function getTokenAccounts (
    wallet: PublicKey,
    connect: anchor.web3.Connection
): Promise<any> {

    const tokenAccounts = connect.getTokenAccountsByOwner(
        wallet,
        {programId: TOKEN_PROGRAM_ID},
    ).then(e => e.value)

    console.log(tokenAccounts)


    const tokens = () => {
        Promise.all([tokenAccounts]).then(result => { result.values() })
    }
    console.log(tokens)

    return 1
}

